mapper.select:
select * from message
where receiverAccount = #{receiverAccount} and senderAccount = #{senderAccount}
or receiverAccount = #{senderAccount} and senderAccount = #{receiverAccount}
order by createTime
limit #{page},10

select count(*) from message
where receiverAccount = #{receiverAccount} and senderAccount = #{senderAccount}
or receiverAccount = #{senderAccount} and senderAccount = #{receiverAccount}
